import { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Switch, Link, Route } from 'react-router-dom'
import * as Ethereum from './services/Ethereum'
import styles from './App.module.css'
import MediumEditor from 'medium-editor'
import 'medium-editor/dist/css/medium-editor.css'
import 'medium-editor/dist/css/themes/default.css'
import Web3 from 'web3'

function Wikipedia(Wikipedia){
    this.web3 = null; 
    this.instance = null; 
    this.Wikipedia = Wikipedia; 
}

Wikipedia.prototype.init = function(){
   // Creates a new Web3 instance using a provider
    // Learn more: https://web3js.readthedocs.io/en/v1.2.0/web3.html
  this.web3 = new Web3(
    (window.web3 && window.web3.currentProvider) || new Web3.providers.HttpProvider(this.Wikipedia.endpoint)
  ); 

     // Creates the contract interface using the web3.js contract object
    // Learn more: https://web3js.readthedocs.io/en/v1.2.0/web3-eth-contract.html#new-contract
    var contract_interface = this.web3.eth.contract(this.Wiki.abi);

    // Defines the address of the contract instance 
    this.instance = this.Wikipedia.address ? contract_interface.at(this.Wikipedia.address): {message: () => {}}; 
};

Wikipedia.prototype.getArticle = function(id){
  return this.instance.articlesById[id]; 
}

const NewArticle = () => {
  const [editor, setEditor] = useState(null)
  useEffect(() => {
    setEditor(new MediumEditor(`.${styles.editable}`))
  }, [setEditor])
  return (
    <form>
      <div className={styles.subTitle}>New article</div>
      <div className={styles.mediumWrapper}>
        <textarea className={styles.editable} />
      </div>
      <input type="submit" value="Submit" />
    </form>
  )
}

const Home = () => {
  return (
    <div className={styles.links}>
      <Link to="/">Home</Link>
      <p>Holaa</p>
      <Link to="/article/new">Add an article</Link>
      <Link to="/article/all">All articles</Link>
      <Link to="/article/read">Read an article</Link>
      <Link to="/article/update">Update an articles</Link>
    </div>
  )
}

const AllArticles = () => {
  const [articles, setArticles] = useState([])
  const contract = useSelector(({ contract }) => contract)
  useEffect(() => {
    if (contract) {
      contract.methods.articleContent(0).call().then(console.log)
      contract.methods.getAllIds().call().then(console.log)
    }
  }, [contract, setArticles])
  return <div>{articles.map(article => article)}</div>
}

const NotFound = () => {
  return <div>Not found</div>
}

const ReadArticle = () => {
  const [articles, setArticles] = useState([])
  const contract = useSelector(({ contract }) => contract)
  useEffect(() => {
    if (contract) {
      contract.methods.articleContent(0).call().then(console.log)
      contract.methods.getAllIds().call().then(console.log)
    }
  }, [contract, setArticles])
  return <div>{articles.map(article => article)}</div>
}

const UpdateArticle = () => {
  const [articles, setArticles] = useState([])
  const contract = useSelector(({ contract }) => contract)
  useEffect(() => {
    if (contract) {
      contract.methods.articleContent(0).call().then(console.log)
      contract.methods.getAllIds().call().then(console.log)
    }
  }, [contract, setArticles])
  return <div>{articles.map(article => article)}</div>
}

const App = () => {
  const dispatch = useDispatch()
  useEffect(() => {
    dispatch(Ethereum.connect)
  }, [dispatch])
  return (
    <div className={styles.app}>
      <div className={styles.title}>Welcome to Decentralized Wikipedia</div>
      <p>Toto</p>
      <Switch>
        <Route path="/article/new">
          <NewArticle />
        </Route>

        <Route exact path="/">
          <Home />
        </Route>

        <Route path="/article/all">
          <AllArticles />
        </Route>

        <Route path="/article/read">
          <ReadArticle />
        </Route>

        <Route path="/article/update">
          <UpdateArticle />
        </Route>

        <Route>
          <NotFound />
        </Route>

      </Switch>
    </div>
  )
}

export default App
